package com.Switch;

public class CharIntSwitch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int input = 97;
		
		switch (input) {
		case 'a' :
			System.out.println("a");
			break;
		case 'z' :
			System.out.println("z");
			break;
		}
	}

}
